import { useState } from "react";
const CakeForm = () => {

    const [name, setName] = useState("")
    const [ingredients, setIngredients] = useState("")
    const [price, setPrice] = useState("")
    const [rating, setRating] = useState("")

    const updateName = (event) =>{
        setName(event.target.value)
    }
    const updateIngredients = (event) =>{
        setIngredients(event.target.value)
    }
    const updatePrice = (event) =>{
        setPrice(event.target.value)
    }
    const updateRating = (event) =>{
        setRating(event.target.value)
    }



    return ( 

        <section>

            <h2>Add new cakes here !</h2>
            <form>
            <label htmlFor="name-input"></label>
            <input
            type="text"
            id ="name-input"
            onInput={(event) => updateName (event)}
            value = {name}
            />
            <label htmlFor="ingredients-input"></label>
            <input
            type="text"
            id ="ingredients-input"
            onInput={(event) => updateIngredients (event)}
            value = {ingredients}
            />
            <label htmlFor="price-input"></label>
            <input
            type="text"
            id ="price-input"
            onInput={(event) => updatePrice (event)}
            value = {price}
            />
            <label htmlFor="rating-input"></label>
            <input
            type="text"
            id ="rating-input"
            onInput={(event) => updateRating (event)}
            value = {rating}
            />
            </form>



        </section>

     );
}

export default CakeForm;
